import type { CommentStorageAdapter, StorageAdapterConfig } from "./comment-storage-adapter"
import { LocalStorageAdapter } from "./local-storage-adapter"
import { ServerActionAdapter } from "./server-action-adapter"
import { CachedServerActionAdapter, type ServerActionSet, type CachedServerActionAdapterConfig } from "./cached-server-action-adapter"
import { ApiAdapter } from "./api-adapter"
import { useTanstackQueryAdapter } from "./tanstack-query-adapter"

export type { CommentStorageAdapter, StorageAdapterConfig, ServerActionSet, CachedServerActionAdapterConfig }
export { LocalStorageAdapter }
export { ServerActionAdapter }
export { CachedServerActionAdapter }
export { ApiAdapter }
export { useTanstackQueryAdapter }

export function createStorageAdapter(
  type: "localStorage" | "serverActions" | "cachedServerActions" | "api" | "custom",
  config: StorageAdapterConfig | CachedServerActionAdapterConfig = {},
  customAdapter?: CommentStorageAdapter,
): CommentStorageAdapter {
  switch (type) {
    case "localStorage":
      return new LocalStorageAdapter(config)
    case "serverActions":
      return new ServerActionAdapter(config)
    case "cachedServerActions":
      return new CachedServerActionAdapter(config as CachedServerActionAdapterConfig)
    case "api":
      return new ApiAdapter(config)
    case "custom":
      if (!customAdapter) {
        throw new Error("Custom adapter must be provided when type is 'custom'")
      }
      return customAdapter
    default:
      throw new Error(`Unknown adapter type: ${type}`)
  }
}

import type {
    Comment,
    CommentThread,
    User,
    MentionUser,
    MentionTag,
} from "@/lib/comments/types/comments";
import type {
    CommentStorageAdapter,
    StorageAdapterConfig,
} from "./comment-storage-adapter";
import { generateId } from "@/lib/comments/utils/generateId";
import { extractMentionsAndTags } from "@/lib/comments/lexical-utils";
import { debug } from "@/lib/comments/utils/debug";

export class LocalStorageAdapter implements CommentStorageAdapter {
  private readonly STORAGE_KEYS = {
    COMMENTS: "fargo_comment_store",
  } as const;

  constructor(private config: StorageAdapterConfig = {}) {}

  async getComments(): Promise<Comment[]> {
    if (typeof window === "undefined") return [];
    const stored = localStorage.getItem(this.STORAGE_KEYS.COMMENTS);
    if (!stored) return [];

    const comments = JSON.parse(stored);
    return comments.map((comment: Comment) => ({
      ...comment,
      createdAt: new Date(comment.createdAt),
      updatedAt: new Date(comment.updatedAt),
    }));
  }

  async saveComments(comments: Comment[]): Promise<void> {
    if (typeof window === "undefined") return;
    localStorage.setItem(this.STORAGE_KEYS.COMMENTS, JSON.stringify(comments));
  }

  async addComment(comment: Comment): Promise<void> {
    const comments = await this.getComments();
    comments.push({
      ...comment,
      createdAt: new Date(),
      updatedAt: new Date(),
    });
    await this.saveComments(comments);
  }

  async updateComment(
    commentId: string,
    updates: Partial<Comment>
  ): Promise<void> {
    const comments = await this.getComments();
    const index = comments.findIndex((c) => c.id === commentId);
    if (index !== -1) {
      comments[index] = {
        ...comments[index],
        ...updates,
        updatedAt: new Date(),
        isEdited: true,
      };
      await this.saveComments(comments);
    }
  }

  async deleteComment(commentId: string): Promise<void> {
    const comments = await this.getComments();
    const filtered = comments.filter((c) => c.id !== commentId);
    await this.saveComments(filtered);
  }

  async addLexicalComment(
    content: string,
    editorState: string,
    author: User,
    mentions: MentionUser[] = [],
    tags: MentionTag[] = [],
    sourceId?: string,
    sourceType?: string,
    parentId?: string
  ): Promise<Comment> {
    const extracted = extractMentionsAndTags(editorState);
    const finalMentions = mentions.length > 0 ? mentions : extracted.mentions;
    const finalTags = tags.length > 0 ? tags : extracted.tags;

    const comment: Comment = {
      id: generateId(),
      content,
      editorState,
      authorId: author.id,
      author,
      sourceId,
      sourceType,
      parentId,
      createdAt: new Date(),
      updatedAt: new Date(),
      isEdited: false,
      mentions: finalMentions,
      tags: finalTags,
      reactions: [],
      status: "active",
    };

    await this.addComment(comment);
    debug.log("Comment added to storage:", comment);
    return comment;
  }

  async updateCommentWithEditorState(
    commentId: string,
    content: string,
    editorState: string,
    mentions?: MentionUser[],
    tags?: MentionTag[]
  ): Promise<void> {
    const comments = await this.getComments();
    const index = comments.findIndex((c) => c.id === commentId);
    if (index !== -1) {
      const extracted = extractMentionsAndTags(editorState);
      const finalMentions = mentions || extracted.mentions;
      const finalTags = tags || extracted.tags;

      comments[index] = {
        ...comments[index],
        content,
        editorState,
        mentions: finalMentions,
        tags: finalTags,
        updatedAt: new Date(),
        isEdited: true,
      };
      await this.saveComments(comments);
    }
  }

  async getcommentsSource(
    sourceId: string,
    sourceType?: string
  ): Promise<Comment[]> {
    const comments = await this.getComments();
    return comments.filter((c) => {
      if (sourceType) {
        return c.sourceId === sourceId && c.sourceType === sourceType;
      }
      return c.sourceId === sourceId;
    });
  }

  async getCommentThreads(
    sourceId?: string,
    sourceType?: string
  ): Promise<CommentThread[]> {
    const comments = sourceId
      ? await this.getcommentsSource(sourceId, sourceType)
      : await this.getComments();

    const rootComments = comments.filter((c) => !c.parentId);

    return rootComments.map((rootComment) => {
      const replies = comments.filter((c) => c.parentId === rootComment.id);
      return {
        id: rootComment.id,
        rootComment,
        replies,
        totalReplies: replies.length,
        lastActivity:
          replies.length > 0
            ? new Date(Math.max(...replies.map((r) => r.createdAt.getTime())))
            : rootComment.createdAt,
      };
    });
  }

  async clearAllStorage(): Promise<void> {
    if (typeof window === "undefined") return;
    localStorage.removeItem(this.STORAGE_KEYS.COMMENTS);
  }
}

"use client"

import React from "react";

import type { Comment, CommentReaction, User } from "@/lib/comments/types/comments";

// Event types that can be emitted
export interface CommentEventMap {
  "comment:added": { comment: Comment; user: User }
  "comment:updated": { comment: Comment; previousContent: string; user: User }
  "comment:deleted": { commentId: string; user: User }
  "reaction:added": { commentId: string; reaction: CommentReaction; user: User }
  "reaction:removed": { commentId: string; reactionId: string; user: User }
  "comments:loaded": { comments: Comment[] }
  "comments:cleared": { user: User }
  error: { error: string; action?: string }
}

// Event listener type
export type CommentEventListener<T extends keyof CommentEventMap> = (
  data: CommentEventMap[T]
) => void

// Event emitter class for comment system
export class CommentEventEmitter {
  private listeners: Map<
    keyof CommentEventMap,
    Set<CommentEventListener<keyof CommentEventMap>>
  > = new Map()

  // Subscribe to an event
  on<T extends keyof CommentEventMap>(
    event: T,
    listener: CommentEventListener<T>
  ): () => void {
    if (!this.listeners.has(event)) {
      this.listeners.set(event, new Set())
    }

    this.listeners.get(event)!.add(listener as CommentEventListener<keyof CommentEventMap>)

    // Return unsubscribe function
    return () => {
      this.listeners.get(event)?.delete(listener as CommentEventListener<keyof CommentEventMap>)
    }
  }

  // Emit an event
  emit<T extends keyof CommentEventMap>(event: T, data: CommentEventMap[T]): void {
    const eventListeners = this.listeners.get(event)
    if (eventListeners) {
      eventListeners.forEach((listener) => {
        try {
          listener(data)
        } catch (error) {
          console.error(`Error in comment event listener for ${event}:`, error)
        }
      })
    }
  }

  // Remove all listeners for an event
  off<T extends keyof CommentEventMap>(event: T): void {
    this.listeners.delete(event)
  }

  // Remove all listeners
  removeAllListeners(): void {
    this.listeners.clear()
  }

  // Get listener count for debugging
  getListenerCount<T extends keyof CommentEventMap>(event: T): number {
    return this.listeners.get(event)?.size || 0
  }
}

// Create a singleton instance for the comment system
export const commentEvents = new CommentEventEmitter()

// Helper hook for subscribing to events in React components
export function useCommentEvent<T extends keyof CommentEventMap>(
  event: T,
  listener: CommentEventListener<T>
): void {
  React.useEffect(() => {
    const unsubscribe = commentEvents.on(event, listener)
    return unsubscribe
  }, [event, listener])
}

"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  Heart,
  MessageCircle,
  Edit,
  Trash2,
  BookText,
  X,
  ThumbsUp,
  Share2Icon,
  ForwardIcon,
  CheckCircle2Icon,
  MessageCircleXIcon,
} from "lucide-react";
import { DeleteConfirmationDialog } from "@/lib/comments/components/comments/delete-confirmation-dialog";
import type { Comment, User as UserType } from "@/lib/comments/types/comments";
import { type CommentVariant } from "@/lib/comments/types/comments";

interface CommentActionBarProps {
  comment: Comment;
  currentUser: UserType;
  variant: CommentVariant;
  isReply: boolean;
  isEditing: boolean;
  isOwner: boolean;
  isReplyingTo?: boolean;
  onEdit?: (commentId: string, content: string, editorState: string) => void;
  onDelete?: () => void;
  onReply?: () => void;
  onReplyCancel?: () => void;
  onLike?: () => void;
  onShare?: () => void;
  onForward?: () => void;
  onApprove?: () => void;
  onReact?: (reaction: string) => void;
  onToggleEdit?: () => void;
  replies?: Comment[];
}

export function CommentActionBar({
  comment,
  currentUser,
  variant,
  isReply,
  isEditing,
  isOwner,
  isReplyingTo = false,
  onEdit,
  onDelete,
  onReply,
  onReplyCancel,
  onLike,
  onShare,
  onForward,
  onApprove,
  onReact,
  onToggleEdit,
  replies = [],
}: CommentActionBarProps) {
  // The `currentUser`, `isReply`, and `onEdit` props are passed for consistency with other variants,
  // but are not used in this component.
  if (currentUser && isReply && onEdit) {
    // do nothing
  }
  const [showReferences, setShowReferences] = useState(false);
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);

  const handleDeleteClick = () => {
    setShowDeleteDialog(true);
  };

  const handleConfirmDelete = () => {
    onDelete?.();
    setShowDeleteDialog(false);
  };

  const getActionBarStyles = () => {
    switch (variant) {
      case "compact":
        return "flex items-center gap-3 mt-2";
      case "card":
        return "flex items-center gap-4 mt-3 pt-3 border-t border-gray-100";
      case "bubble":
        return "flex items-center gap-2 mt-2";
      case "timeline":
        return "flex items-center gap-4 mt-4 pt-3 border-t border-blue-100";
      case "social":
        return "flex items-center justify-start gap-4 mt-4 pt-3 border-t border-gray-100";
      case "professional":
        return "flex items-center gap-4 mt-3 pt-3 border-t border-gray-200";
      case "clean":
        return "flex items-center gap-3 mt-2";
      case "github":
        return "flex items-center gap-2 mt-2";
      case "email":
        return "flex items-center gap-3 mt-3 pt-2 border-t border-gray-100";
      case "notion":
        return "flex items-center gap-2 mt-2 opacity-0 group-hover:opacity-100 transition-opacity";
      case "mobile":
        return "flex items-center gap-2 mt-4";
      case "thread":
        return "flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity duration-150";
      case "plain":
        return "flex items-center gap-3 mt-2";
      default:
        return "flex items-center gap-3 mt-2";
    }
  };

  const getButtonStyles = (action?: string) => {
    const baseStyles = getBaseButtonStyles();
    const actionStyles = getActionSpecificStyles(action);
    return `${baseStyles} ${actionStyles}`;
  };

  const getBaseButtonStyles = () => {
    switch (variant) {
      case "compact":
        return "h-6 px-2 text-xs";
      case "card":
        return "h-8 px-3 text-sm";
      case "bubble":
        return "h-7 px-2 text-xs";
      case "timeline":
        return "h-8 px-2 text-sm";
      case "social":
        return "h-8 px-3 text-sm";
      case "professional":
        return "h-8 px-4 text-sm";
      case "clean":
        return "h-7 px-2 text-sm";
      case "github":
        return "h-6 px-2 text-xs";
      case "email":
        return "h-7 px-3 text-sm";
      case "notion":
        return "h-6 px-2 text-xs";
      case "mobile":
        return "h-9 px-3 text-sm rounded-full flex-grow";
      case "thread":
        return "h-6 px-2 text-xs";
      case "plain":
        return "h-6 px-2 text-sm";
      default:
        return "h-6 px-2 text-xs";
    }
  };

  const getActionSpecificStyles = (action?: string) => {
    switch (variant) {
      case "professional":
        switch (action) {
          case "approve":
            return "text-slate-600 hover:text-blue-600 hover:bg-blue-50";
          case "delete":
            return "text-slate-600 hover:text-red-600 hover:bg-red-50";
          default:
            return "text-slate-600 hover:text-slate-800 hover:bg-slate-100";
        }
      case "thread":
        switch (action) {
          case "like":
            return "text-gray-500 hover:text-blue-600 hover:bg-blue-50";
          case "reply":
            return "text-gray-500 hover:text-green-600 hover:bg-green-50";
          case "delete":
            return "text-gray-500 hover:text-red-600 hover:bg-red-50";
          default:
            return "text-gray-500 hover:text-blue-600 hover:bg-blue-50";
        }
      case "social":
        switch (action) {
          case "like":
            return "text-gray-600 hover:text-pink-600 hover:bg-pink-50";
          case "reply":
            return "text-gray-600 hover:text-blue-600 hover:bg-blue-50";
          case "share":
            return "text-gray-600 hover:text-green-600 hover:bg-green-50";
          case "delete":
            return "text-gray-600 hover:text-red-600 hover:bg-red-50";
          default:
            return "text-gray-600 hover:text-gray-800";
        }
      case "notion":
        switch (action) {
          case "like":
            return "text-gray-500 hover:text-orange-600 hover:bg-orange-50";
          case "delete":
            return "text-gray-500 hover:text-red-600 hover:bg-red-50";
          default:
            return "text-gray-500 hover:text-gray-700 hover:bg-gray-100";
        }
      case "compact":
        return "text-gray-500 hover:text-gray-700";
      case "card":
        return "text-gray-600 hover:text-gray-800";
      case "bubble":
        return "text-blue-600 hover:text-blue-800";
      case "timeline":
        return "text-blue-600 hover:text-blue-800";
      case "clean":
        return "text-gray-500 hover:text-gray-700";
      case "github":
        return "text-gray-600 hover:text-gray-800";
      case "email":
        return "text-gray-600 hover:text-gray-800";
      case "mobile":
        switch (action) {
          case "like":
            return "text-gray-600 hover:text-pink-600 hover:bg-pink-50";
          case "reply":
            return "text-gray-600 hover:text-blue-600 hover:bg-blue-50";
          case "edit":
            return "text-gray-600 hover:text-gray-800 hover:bg-gray-100";
          case "delete":
            return "text-gray-600 hover:text-red-600 hover:bg-red-50";
          default:
            return "text-gray-600 hover:text-gray-800 hover:bg-gray-100";
        }
      case "plain":
        return "text-gray-500 hover:text-gray-700";
      default:
        return "text-gray-500 hover:text-gray-700";
    }
  };

  const getIconSize = () => {
    switch (variant) {
      case "mobile":
        return "w-5 h-5";
      case "social":
        return "w-4 h-4";
      case "professional":
        return "w-4 h-4";
      case "thread":
        return "w-3 h-3";
      case "notion":
        return "w-3 h-3";
      default:
        return "w-3 h-3";
    }
  };

  const getIconMargin = (showText: boolean) => {
    if (!showText) return "mr-0";

    switch (variant) {
      case "professional":
      case "social":
        return "mr-2";
      case "thread":
        return "mr-1";
      default:
        return "mr-1";
    }
  };

  const showTextLabels = () => {
    switch (variant) {
      case "compact":
      case "card":
      case "mobile":
      case "social":
      case "email":
      case "professional":
      case "github":
        return true;
      case "bubble":
      case "timeline":
      case "clean":
      case "notion":
      case "thread":
      case "plain":
        return false;
      default:
        return true;
    }
  };

  const iconSize = getIconSize();
  const showText = showTextLabels();
  const iconMargin = getIconMargin(showText);

  // Hide actions when editing (except toggle edit button)
  if (isEditing) {
    return (
      <div className={getActionBarStyles()}>
        {isOwner && onToggleEdit && (
          <Button
            variant="ghost"
            size="sm"
            onClick={onToggleEdit}
            className={getButtonStyles()}
          >
            <X className={`${iconSize} ${iconMargin}`} />
            Close
          </Button>
        )}
      </div>
    );
  }

  return (
    <>
      <div className={getActionBarStyles()}>
        {onReact && variant === "github" && (
          <Button
            variant="ghost"
            size="sm"
            onClick={() => onReact("üëç")}
            className={getButtonStyles("react")}
          >
            <ThumbsUp className={`${iconSize} ${iconMargin}`} />
            {showText ? "üëç React" : "üëç"}
          </Button>
        )}

        {onLike && (
          <Button
            variant="ghost"
            size="sm"
            onClick={onLike}
            className={getButtonStyles("like")}
          >
            <Heart className={`${iconSize} ${iconMargin}`} />
            {showText ? "Like" : ""}
          </Button>
        )}

        {(onReply || onReplyCancel) && (
          <Button
            variant="ghost"
            size="sm"
            onClick={isReplyingTo ? onReplyCancel : onReply}
            className={getButtonStyles("reply")}
          >
            {isReplyingTo ? (
              <MessageCircleXIcon className={`${iconSize} ${iconMargin}`} />
            ) : (
              <MessageCircle className={`${iconSize} ${iconMargin}`} />
            )}
            {showText ? (isReplyingTo ? "Cancel" : "Reply") : ""}
          </Button>
        )}

        {comment.sourceReference && (
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setShowReferences(!showReferences)}
            className={`${getButtonStyles("reference")} ${
              showReferences ? "text-purple-600 bg-purple-50" : ""
            }`}
          >
            <BookText className={`${iconSize} ${iconMargin}`} />
            {showText ? "References" : ""}
          </Button>
        )}

        {onShare && variant === "social" && (
          <Button
            variant="ghost"
            size="sm"
            onClick={onShare}
            className={getButtonStyles("share")}
          >
            <Share2Icon className={`${iconSize} ${iconMargin}`} />
            {showText ? "Share" : ""}
          </Button>
        )}

        {onForward && variant === "email" && (
          <Button
            variant="ghost"
            size="sm"
            onClick={onForward}
            className={getButtonStyles()}
          >
            <ForwardIcon className={`${iconSize} ${iconMargin}`} />
            {showText ? "Forward" : ""}
          </Button>
        )}

        {onApprove && variant === "professional" && (
          <Button
            variant="ghost"
            size="sm"
            onClick={onApprove}
            className={getButtonStyles("approve")}
          >
            <CheckCircle2Icon className={`${iconSize} ${iconMargin}`} />
            {showText ? "Approve" : ""}
          </Button>
        )}

        {isOwner && (
          <>
            {onToggleEdit && (
              <Button
                variant="ghost"
                size="sm"
                onClick={onToggleEdit}
                className={getButtonStyles("edit")}
              >
                <Edit className={`${iconSize} ${iconMargin}`} />
                {showText ? "Edit" : ""}
              </Button>
            )}

            <Button
              variant="ghost"
              size="sm"
              onClick={handleDeleteClick}
              className={getButtonStyles("delete")}
            >
              <Trash2 className={`${iconSize} ${iconMargin}`} />
              {showText ? "Delete" : ""}
            </Button>
          </>
        )}
      </div>

      {/* Source Reference Display */}
      {comment.sourceReference && showReferences && (
        <div
          className={
            variant === "github"
              ? "mt-3 p-3 bg-blue-50 border border-blue-200 rounded-lg"
              : variant === "professional"
              ? "mt-4 p-3 bg-blue-50 border border-blue-200 rounded-lg"
              : variant === "thread"
              ? "mb-2 p-2 bg-blue-50 border border-blue-200 rounded-md"
              : variant === "social"
              ? "mt-3 p-3 bg-blue-50 border border-blue-200 rounded-lg"
              : variant === "notion"
              ? "mt-3 p-3 bg-orange-50 border border-orange-200 rounded-md"
              : "mt-2 p-2 bg-blue-50 border border-blue-200 rounded text-xs"
          }
        >
          <div className="flex items-start gap-2">
            <div
              className={
                variant === "github"
                  ? "w-2 h-2 bg-blue-500 rounded-full mt-2 flex-shrink-0"
                  : variant === "thread"
                  ? "w-1.5 h-1.5 bg-blue-500 rounded-full"
                  : variant === "social"
                  ? "w-2 h-2 bg-blue-500 rounded-full"
                  : variant === "notion"
                  ? "w-1.5 h-1.5 bg-orange-500 rounded-full mt-2 flex-shrink-0"
                  : "w-1 h-1 rounded-full bg-blue-400 mt-1.5 flex-shrink-0"
              }
            ></div>
            <div className="flex-1">
              {variant === "notion" ? (
                <>
                  <div className="text-xs font-medium text-orange-700 mb-1">
                    Referenced:
                  </div>
                  <div className="text-sm font-medium text-orange-900">
                    {comment.sourceReference.label}
                  </div>
                </>
              ) : (
                <>
                  <span className="text-blue-700 font-medium">
                    Referenced:{" "}
                  </span>
                  <span className="text-blue-800">
                    {comment.sourceReference.label}
                  </span>
                </>
              )}
              {comment.sourceReference.description && (
                <div className="text-blue-600 mt-1">
                  {comment.sourceReference.description}
                </div>
              )}
              {comment.sourceReference.url && (
                <a
                  href={comment.sourceReference.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 hover:text-blue-800 underline mt-1 inline-block"
                >
                  View Source ÔøΩ
                </a>
              )}
            </div>
          </div>
        </div>
      )}

      <DeleteConfirmationDialog
        isOpen={showDeleteDialog}
        onClose={() => setShowDeleteDialog(false)}
        onConfirm={handleConfirmDelete}
        commentAuthor={comment.author.name}
        hasReplies={replies.length > 0}
        replyCount={replies.length}
      />
    </>
  );
}

"use client"

import Link from "next/link";
import { ExternalLink } from "lucide-react";
import type { Comment } from "@/lib/comments/types/comments";
import { type CommentVariant } from "@/lib/comments/types/comments";

interface CommentSourceReferenceProps {
  sourceReference: Comment["sourceReference"]
  variant: CommentVariant
  className?: string
}

export function CommentSourceReference({
  sourceReference,
  variant,
  className = "",
}: CommentSourceReferenceProps) {
  if (!sourceReference) return null

  const getVariantStyles = () => {
    switch (variant) {
      case "clean":
