        onReplyCancel={onReplyCancel}
        onForward={handleForward}
      />
    )
  }

  if (variant === "bubble") {
    return (
      <BubbleVariant
        comment={comment}
        currentUser={currentUser}
        isReply={isReply}
        isEditing={isEditing}
        setIsEditing={setIsEditing}
        isReplyingTo={isReplyingTo}
        onEdit={onEdit}
        onDelete={handleDelete}
        onReply={handleReply}
        onReplyCancel={onReplyCancel}
        onLike={handleLike}
      />
    )
  }

  if (variant === "timeline") {
    return (
      <TimelineVariant
        comment={comment}
        currentUser={currentUser}
        isReply={isReply}
        isEditing={isEditing}
        setIsEditing={setIsEditing}
        isReplyingTo={isReplyingTo}
        onEdit={onEdit}
        onDelete={handleDelete}
        onReply={handleReply}
        onReplyCancel={onReplyCancel}
        onLike={handleLike}
      />
    )
  }

  if (variant === "social") {
    return (
      <SocialVariant
        comment={comment}
        currentUser={currentUser}
        isReply={isReply}
        isEditing={isEditing}
        setIsEditing={setIsEditing}
        isReplyingTo={isReplyingTo}
        onEdit={onEdit}
        onDelete={handleDelete}
        onReply={handleReply}
        onReplyCancel={onReplyCancel}
        onLike={handleLike}
        onShare={handleShare}
      />
    )
  }

  if (variant === "thread") {
    return (
      <ThreadVariant
        comment={comment}
        currentUser={currentUser}
        isReply={isReply}
        isEditing={isEditing}
        setIsEditing={setIsEditing}
        isReplyingTo={isReplyingTo}
        onEdit={onEdit}
        onDelete={handleDelete}
        onReply={handleReply}
        onReplyCancel={onReplyCancel}
        onLike={handleLike}
      />
    )
  }

  if (variant === "professional") {
    return (
      <ProfessionalVariant
        comment={comment}
        currentUser={currentUser}
        isReply={isReply}
        isEditing={isEditing}
        setIsEditing={setIsEditing}
        isReplyingTo={isReplyingTo}
        onEdit={onEdit}
        onDelete={handleDelete}
        onReply={handleReply}
        onReplyCancel={onReplyCancel}
        onApprove={handleApprove}
      />
    )
  }

  if (variant === "mobile") {
    return (
      <MobileVariant
        comment={comment}
        currentUser={currentUser}
        isReply={isReply}
        isEditing={isEditing}
        setIsEditing={setIsEditing}
        isReplyingTo={isReplyingTo}
        onEdit={onEdit}
        onDelete={handleDelete}
        onReply={handleReply}
        onReplyCancel={onReplyCancel}
        onLike={handleLike}
      />
    )
  }

  if (variant === "notion") {
    return (
      <NotionVariant
        comment={comment}
        currentUser={currentUser}
        isReply={isReply}
        isEditing={isEditing}
        setIsEditing={setIsEditing}
        isReplyingTo={isReplyingTo}
        onEdit={onEdit}
        onDelete={handleDelete}
        onReply={handleReply}
        onReplyCancel={onReplyCancel}
        onLike={handleLike}
      />
    )
  }

  if (variant === "clean") {
    return (
      <CleanVariant
        comment={comment}
