          {item.data?.email && (
            <div className="text-sm text-gray-500 truncate">
              {item.data.email}
            </div>
          )}
          {item.data?.description && (
            <div className="text-sm text-gray-500 truncate">
              {item.data.description}
            </div>
          )}
        </div>
      </div>
    </li>
  );
});
