            }
            placeholder={null}
            ErrorBoundary={LexicalErrorBoundary}
          />
          <HistoryPlugin />
          {features.lists !== false && <ListPlugin />}
          {features.checkLists !== false && <CheckListPlugin />}
          {features.mentions !== false && !mentionLoading && !mentionError && (
            <BeautifulMentionsPlugin
              items={mentionItems}
              menuComponent={CustomMenu}
              menuItemComponent={CustomMenuItem}
            />
          )}
          <ContentExtractor
            onContentChange={setCurrentContent}
            onEditorStateChange={setCurrentEditorState}
            initialContent={initialContent}
            initialEditorState={initialEditorState}
          />
        </div>
        {effectiveVariant !== "inline" && (
          <div className="flex items-center justify-between mt-3 pt-3 border-t border-gray-100">
            <TooltipProvider>
              <Tooltip
                open={showTooltip}
                onOpenChange={setShowTooltip}
              >
                <TooltipTrigger asChild>
                  <Button
                    variant="ghost"
                    size="sm"
                    className="h-7 w-7 p-1 text-gray-500 hover:text-gray-700"
                    onTouchStart={() => setShowTooltip(true)}
                    onTouchEnd={(e) => {
                      e.preventDefault();
                      setTimeout(() => setShowTooltip(false), 3000);
                    }}
                    onClick={() => setShowTooltip(!showTooltip)}
                  >
                    <HelpCircle className="w-4 h-4" />
                  </Button>
                </TooltipTrigger>
                <TooltipContent
                  side="top"
                  align="start"
                  className="max-w-xs"
                >
                  <div className="text-sm">
                    <p className="font-medium mb-2">Start typing...</p>
                    <ul className="space-y-1 text-xs">
                      {features.mentions !== false && (
                        <>
                          <li>‚Ä¢ @ to mention users</li>
                          <li>‚Ä¢ # to reference resources</li>
                        </>
                      )}
                      {features.lists !== false && (
                        <>
                          <li>‚Ä¢ - for bullet lists</li>
                          <li>‚Ä¢ 1. for numbered lists</li>
                        </>
                      )}
                      {features.autoLink !== false && (
                        <li>‚Ä¢ URLs will auto-link</li>
                      )}
                      {features.emoji !== false && <li>‚Ä¢ : for emojis</li>}
                    </ul>
                    {mentionLoading && (
                      <p className="text-xs text-amber-600 mt-2">
                        Loading mentions...
                      </p>
                    )}
                    {mentionError && (
                      <p className="text-xs text-red-600 mt-2">
                        Mention loading failed
                      </p>
                    )}
                  </div>
                </TooltipContent>
              </Tooltip>
            </TooltipProvider>
            <Button
              ref={submitButtonRef}
              onClick={handleSubmit}
              size={buttonConfig.size}
              className={`flex items-center gap-2 ${
                buttonConfig.className || ""
              }`}
              disabled={!currentContent.trim()}
            >
              {buttonConfig.showIcon && <Send className="w-4 h-4" />}
              {buttonConfig.showText && buttonConfig.text}
            </Button>
