  }

  return (
    <div className={`lexical-read-only ${className}`}>
      <LexicalComposer initialConfig={initialConfig}>
        <RichTextPlugin
          contentEditable={
            <ContentEditable className="lexical-content-editable outline-none resize-none overflow-hidden" />
          }
