        />
        <HistoryPlugin />
        <ListPlugin />
        <CheckListPlugin />
        <BeautifulMentionsPlugin items={mentionItems} menuComponent={() => null} menuItemComponent={() => null} />
        <EditorStateLoader editorState={editorState} content={content} />
      </LexicalComposer>
