import { useState, useMemo, useCallback } from "react"
import { debug } from "@/lib/comments/utils/debug"
import type { CommentHooks, CommentHookRegistry, CommentHookContext } from "@/lib/comments/types/comment-hooks"
import type { User } from "@/lib/comments/types/comments"
import type { CommentConfig } from "@/hooks/use-comment-config"
import type { CommentState } from "@/lib/comments/reducers/comment-reducer"
import type { CommentEventEmitter } from "@/lib/comments/comment-events"

interface UseCommentContextHooksProps {
  initialHooks?: Partial<CommentHooks>
  currentUser: User | null
  config: CommentConfig
  state: CommentState
  events: CommentEventEmitter
}

export function useCommentContextHooks({
  initialHooks,
  currentUser,
  config,
  state,
  events,
}: UseCommentContextHooksProps) {
  const [registeredHooks, setRegisteredHooks] = useState<CommentHooks>(() => {
    const hooks: CommentHooks = {}
    if (initialHooks) {
      Object.entries(initialHooks).forEach(([key, callbacks]) => {
        if (callbacks) {
          hooks[key as keyof CommentHooks] = Array.isArray(callbacks) ? callbacks : [callbacks]
        }
      })
    }
    return hooks
  })

  const createHookContext = useCallback((): CommentHookContext => {
    return {
      currentUser,
      config,
      existingComments: state.comments,
    }
  }, [currentUser, config, state.comments])

  const hookRegistry: CommentHookRegistry = useMemo(
    () => ({
      registerHook: (hookName, callback) => {
        setRegisteredHooks((prev) => ({
          ...prev,
          [hookName]: [...(prev[hookName] || []), callback],
        }))
        return () => {
          setRegisteredHooks((prev) => ({
            ...prev,
            [hookName]: (prev[hookName] || []).filter((cb) => cb !== callback),
          }))
        }
      },
      unregisterHook: (hookName, callback) => {
        setRegisteredHooks((prev) => ({
          ...prev,
          [hookName]: (prev[hookName] || []).filter((cb) => cb !== callback),
        }))
      },
      executeHooks: async (hookName, data, context) => {
        const hooks = registeredHooks[hookName] || []
        let result = data

        for (const hook of hooks) {
          try {
            const hookResult = await hook(result, context)
            if (hookResult && typeof hookResult === "object") {
              result = { ...result, ...hookResult }
            }
          } catch (error) {
            debug.error(`Hook ${hookName} failed:`, error)
            events.emit("error", {
              error: `Hook ${hookName} failed: ${error}`,
              action: "hook",
            })
          }
        }

        return result
      },
    }),
    [registeredHooks, events],
  )

  return {
    hookRegistry,
    createHookContext,
  }
}

"use client"

import { useMemo } from "react"
import { useComments } from "@/lib/comments/contexts/comment-context"

export function useCommentsFromSource(sourceId: string, sourceType?: string) {
  const { state, getcommentsSource } = useComments()

  const comments = useMemo(() => {
    return getcommentsSource(sourceId, sourceType)
  }, [getcommentsSource, sourceId, sourceType])

  const commentStats = useMemo(() => {
    const unresolvedComments = comments.filter((comment) => !comment.reactions.some((r) => r.type === "resolved"))

    return {
      totalComments: comments.length,
      unresolvedCount: unresolvedComments.length,
      hasComments: comments.length > 0,
      hasUnresolved: unresolvedComments.length > 0,
    }
  }, [comments])

  return {
    comments,
    ...commentStats,
    loading: state.loading,
    error: state.error,
  }
}

import { debug } from "@/lib/comments/utils/debug"

export interface ExtractedMention {
  value: string
  [key: string]: string | number | boolean | undefined
}

export interface ExtractedTag {
  value: string
  [key: string]: string | number | boolean | undefined
}

interface LexicalNode {
  type: string
  value?: string
  data?: Record<string, unknown>
  trigger?: string
  children?: LexicalNode[]
}

export function extractMentionsAndTags(editorState: string): {
  mentions: ExtractedMention[]
  tags: ExtractedTag[]
} {
  try {
    const parsed: { root: LexicalNode } = JSON.parse(editorState)
    const mentions: ExtractedMention[] = []
    const tags: ExtractedTag[] = []

    function traverseNodes(node: LexicalNode) {
      if (node.type === "custom-beautifulMention") {
        const flattened = {
          value: node.value,
          ...(node.data || {}),
        }

        if (node.trigger === "@") {
          mentions.push(flattened as ExtractedMention)
        } else if (node.trigger === "#") {
          tags.push(flattened as ExtractedTag)
        }
      }

      if (node.children && Array.isArray(node.children)) {
        node.children.forEach(traverseNodes)
      }
    }

    traverseNodes(parsed.root)
    return { mentions, tags }
  } catch (error) {
    debug.error("Error extracting mentions/tags from editorState:", error)
    return { mentions: [], tags: [] }
  }
}

import type { Comment, CommentReaction } from "@/lib/comments/types/comments";

// Action types for comment reducer
export type CommentAction =
  | { type: "LOAD_COMMENTS"; payload: Comment[] }
  | { type: "ADD_COMMENT"; payload: Comment }
  | { type: "UPDATE_COMMENT"; payload: { id: string; updates: Partial<Comment> } }
  | { type: "DELETE_COMMENT"; payload: string }
  | { type: "ADD_REACTION"; payload: { commentId: string; reaction: CommentReaction } }
  | { type: "REMOVE_REACTION"; payload: { commentId: string; reactionId: string } }
  | { type: "SET_LOADING"; payload: boolean }
  | { type: "SET_ERROR"; payload: string | null }

// Comment state interface
export interface CommentState {
  comments: Comment[]
  loading: boolean
  error: string | null
}

// Initial state
export const initialCommentState: CommentState = {
  comments: [],
  loading: false,
  error: null,
}

// Comment reducer
export function commentReducer(state: CommentState, action: CommentAction): CommentState {
  switch (action.type) {
    case "LOAD_COMMENTS":
      return {
        ...state,
        comments: action.payload,
        loading: false,
        error: null,
      }

    case "ADD_COMMENT":
      return {
        ...state,
        comments: [...state.comments, action.payload],
        error: null,
      }

    case "UPDATE_COMMENT":
      return {
        ...state,
        comments: state.comments.map((comment) =>
          comment.id === action.payload.id
            ? { ...comment, ...action.payload.updates, isEdited: true, updatedAt: new Date() }
            : comment,
        ),
        error: null,
      }

    case "DELETE_COMMENT":
      return {
        ...state,
        comments: state.comments.filter((comment) => comment.id !== action.payload),
        error: null,
      }

    case "ADD_REACTION":
      return {
        ...state,
        comments: state.comments.map((comment) =>
          comment.id === action.payload.commentId
            ? {
                ...comment,
                reactions: [...comment.reactions, action.payload.reaction],
              }
            : comment,
        ),
        error: null,
      }

    case "REMOVE_REACTION":
      return {
        ...state,
        comments: state.comments.map((comment) =>
          comment.id === action.payload.commentId
            ? {
                ...comment,
                reactions: comment.reactions.filter((r) => r.id !== action.payload.reactionId),
              }
            : comment,
        ),
        error: null,
      }

    case "SET_LOADING":
      return {
        ...state,
        loading: action.payload,
      }

    case "SET_ERROR":
      return {
        ...state,
        error: action.payload,
        loading: false,
      }

    default:
      return state
  }
}

import type { Comment, User, MentionUser, MentionTag } from "@/lib/comments/types/comments"
import type { CommentConfig } from "@/lib/comments/hooks/use-comment-config"

// Hook context provided to all hook callbacks
export interface CommentHookContext {
  currentUser: User | null
  config: CommentConfig
  existingComments: Comment[]
}

// Hook callback types for different operations
export interface AddCommentHookData {
  content: string
  editorState: string
  mentions: MentionUser[]
  tags: MentionTag[]
  sourceId?: string
  sourceType?: string
  parentId?: string
  user: User
}

export interface UpdateCommentHookData {
  commentId: string
  content: string
  editorState: string
  mentions: MentionUser[]
  tags: MentionTag[]
  existingComment: Comment
}

export interface CommentHookData {
  comment: Comment
}

// Hook callback function types
export type AddCommentHook = (
  data: AddCommentHookData,
  context: CommentHookContext,
) => Promise<Partial<AddCommentHookData>> | Partial<AddCommentHookData>

export type UpdateCommentHook = (
  data: UpdateCommentHookData,
  context: CommentHookContext,
) => Promise<Partial<UpdateCommentHookData>> | Partial<UpdateCommentHookData>

export type PreSaveCommentHook = (
  data: CommentHookData,
  context: CommentHookContext,
) => Promise<Partial<Comment>> | Partial<Comment>

export type PostSaveCommentHook = (data: CommentHookData, context: CommentHookContext) => Promise<void> | void

// Hook registry interface
export interface CommentHooks {
  beforeAddComment?: AddCommentHook[]
  afterAddComment?: PostSaveCommentHook[]
  beforeUpdateComment?: UpdateCommentHook[]
  afterUpdateComment?: PostSaveCommentHook[]
  beforeSaveComment?: PreSaveCommentHook[]
  afterSaveComment?: PostSaveCommentHook[]
}

// Hook registration methods
export interface CommentHookRegistry {
  registerHook: <T extends keyof CommentHooks>(hookName: T, callback: NonNullable<CommentHooks[T]>[0]) => () => void
  unregisterHook: <T extends keyof CommentHooks>(hookName: T, callback: NonNullable<CommentHooks[T]>[0]) => void
  executeHooks: <T extends keyof CommentHooks>(
    hookName: T,
    data: Parameters<NonNullable<CommentHooks[T]>[0]>[0],
    context: CommentHookContext,
  ) => Promise<Parameters<NonNullable<CommentHooks[T]>[0]>[0]>
}

